{
    "name": "custom-input-plugin",
    "description": "This plugin transforms pipeline ingestion input into SIF compatible",
    "requiresFileUpload": "true",
    "type": "input",
    "parameters": {
        "key1": "val1"
    }
}

{
    "connectorConfig": {
        "input": [
            {
                "name": "sif-csv-pipeline-input-connector"
            }
        ]
    },
    "attributes": {
        "source": "useeio"
    },
    "tags": {
        "source": "useeio"
    },
    "name": "Household Electricity Carbon Footprint",
    "description": "Pipeline to calculation carbon from electricity consumption",
    "processorOptions": {
        "chunkSize": 1
    },
    "transformer": {
        "transforms": [
            {
                "index": 0,
                "formula": "AS_TIMESTAMP(:reading date,'M/d/yy')",
                "outputs": [
                    {
                        "description": "Timestamp of business activity.",
                        "index": 0,
                        "key": "time",
                        "label": "Time",
                        "type": "timestamp"
                    }
                ]
            },
            {
                "index": 1,
                "formula": ":zipcode",
                "outputs": [
                    {
                        "description": "Zipcode where electricity consumption occurred",
                        "index": 0,
                        "key": "zipcode",
                        "label": "Zip",
                        "type": "string"
                    }
                ]
            },
            {
                "index": 2,
                "formula": ":month",
                "outputs": [
                    {
                        "description": "Month of electricity consumption",
                        "index": 0,
                        "key": "month",
                        "label": "Month",
                        "type": "string"
                    }
                ]
            },
            {
                "index": 3,
                "formula": ":kwh",
                "outputs": [
                    {
                        "description": "kWh of electricity consumption in the month",
                        "index": 0,
                        "key": "kwh",
                        "label": "kWh",
                        "type": "number"
                    }
                ]
            },
            {
                "index": 4,
                "formula": "#electricity_emissions(:kwh,IMPACT(LOOKUP(LOOKUP(LOOKUP(:zipcode, 'ZipcodeToState', 'state'), 'StatePrimaryGen', 'primary_gen'), 'GenToImpact', 'if'), 'co2e', 'co2'))",
                "outputs": [
                    {
                        "description": "CO2e of electricty generation (in tonnes)",
                        "index": 0,
                        "key": "co2e",
                        "label": "CO2e",
                        "type": "number"
                    }
                ]
            }
        ],
        "parameters": [
            {
                "index": 0,
                "key": "zipcode",
                "label": "Zipcode",
                "description": "Zipcode of electricity consumption",
                "type": "string"
            },
            {
                "index": 1,
                "key": "month",
                "label": "Month",
                "description": "Month of electricity generation",
                "type": "string"
            },
            {
                "index": 2,
                "key": "kwh",
                "label": "kWh",
                "description": "kWh of electricity generation in the month",
                "type": "number"
            }
        ]
    }
}

